const HeroTag = () => {
    const [isHovered, setIsHovered] = useState(false);
  return (
    <div
      className="relative transform transition-all duration-500 ease-in-out"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
        {isHovered ? 
        <BeforeHover />
            :
        <Hover />
        }
    </div>
  )
}

